        [TestMethod]
        public void MergeTwoSortedLists()
        {
            //arrange
            var target = new Easy();
            var l1 = new ListNode(-2)
            {
                next = new ListNode(5)
                {
                    // next = new ListNode(5)
                }
            };

            var l2 = new ListNode(-9)
            {
                next = new ListNode(-6)
                {
                    next = new ListNode(-3)
                    {
                        next = new ListNode(-1)
                        {
                            next = new ListNode(1)
                            {
                                next = new ListNode(6)
                            }
                        }
                    }
                }
            };

            var expected = new ListNode(1)
            {
                next = new ListNode(2)
                {
                    next = new ListNode(3)
                    {
                        next = new ListNode(4)
                        {
                            next = new ListNode(5)
                            {
                                next = new ListNode(8)
                            }
                        }
                    }
                }
            };

            //act
            ListNode actual = target.MergeTwoSortedLists(l1, l2);

            //assert
            Assert.Fail();

        }
